import 'package:auto_asig/core/models/user.dart';

double? screenWidth;
double? screenHeight;
const double padding = 32;
const double theFontSize = 20;

String currentUserId = '';
String? fCMToken;

const List<String> weekdays = [
  'Luni',
  'Marți',
  'Miercuri',
  'Joi',
  'Vineri',
  'Sâmbătă',
  'Duminică'
];

const Map<String, int> groups = {
  'General': 0,
  'Grădiniță': 1,
  // 'Adolescenți': 2,
  // 'Adulți': 3,
  // 'Seniori': 4,
};

enum PaymentType {
  monthly,
  anual,
}

extension PaymentTypeExtension on PaymentType {
  static PaymentType fromString(String type) {
    return type == 'anual' ? PaymentType.anual : PaymentType.monthly;
  }
}

enum Months {
  Ianuarie,
  Februarie,
  Martie,
  Aprilie,
  Mai,
  Iunie,
  Iulie,
  August,
  Septembrie,
  Octombrie,
  Noiembrie,
  Decembrie
}

List<UserModel> allMembersGlobal = [];

// Member? currentMember;

UserModel emptyMember = UserModel(
  'NULL',
  fullName: '',
  email: '',
  phone: '',
  country: '',
);
