import 'package:auto_asig/core/data/constants.dart';
import 'package:flutter/material.dart';

class AutoAsigAppbar extends StatelessWidget {
  const AutoAsigAppbar({super.key});

  @override
  Widget build(BuildContext context) {
    return AppBar(
        // backgroundColor: primaryYellow,
        // Change the color of the burger button
        iconTheme: const IconThemeData(
          color: primaryBlue,
        ),
        title: Row(
          children: [
            Image.asset(
              'assets/images/simple_logo.png',
              height: 40,
            ),
            const SizedBox(
                width: 8), // Add some spacing between the logo and the text
            RichText(
              text: const TextSpan(
                children: [
                  TextSpan(
                    text: 'alliat ',
                    style: TextStyle(
                      fontSize: 20,
                      fontWeight: FontWeight.bold,
                      color: Colors.black,
                    ),
                  ),
                  TextSpan(
                    text: 'asigurﾄビi',
                    style: TextStyle(
                      fontSize: 20,
                      fontWeight: FontWeight.bold,
                      color: logoBlue,
                    ),
                  ),
                ],
              ),
            ),
          ],
        ),
        actions: [
          IconButton(
            icon: const Icon(Icons.add),
            color: primaryBlue,
            onPressed: () {
              print('Add button pressed');
            },
          ),
        ],
      );
  }
}
