import 'package:auto_asig/core/cubit/user_data_cubit.dart';
import 'package:auto_asig/core/data/constants.dart';
import 'package:auto_asig/feature/auth/cubit/auth_cubit.dart';
import 'package:auto_asig/feature/auth/screens/onboarding_screen.dart';
import 'package:auto_asig/feature/home_screen/screens/about_screen.dart';
import 'package:flutter/material.dart';
import 'package:flutter_bloc/flutter_bloc.dart';
import 'package:go_router/go_router.dart';

class AutoAsigDrawer extends StatelessWidget {
  const AutoAsigDrawer({super.key});

  @override
  Widget build(BuildContext context) {
    void logout() {
      // ask for confirmation
      showDialog(
        context: context,
        builder: (context) => AlertDialog(
          title: const Text(
            'Delogare',
            style: TextStyle(fontSize: theFontSize),
          ),
          content: const Text(
            'Ești sigur că vrei să te deloghezi?',
            style: TextStyle(
              fontSize: theFontSize,
            ),
          ),
          actions: [
            TextButton(
              onPressed: () => Navigator.of(context).pop(false),
              child: const Text(
                'Nu',
                style: TextStyle(fontSize: theFontSize),
              ),
            ),
            TextButton(
              onPressed: () => Navigator.of(context).pop(true),
              child: const Text(
                'Da',
                style: TextStyle(
                  fontSize: theFontSize,
                ),
              ),
            ),
          ],
        ),
      ).then((value) {
        if (value == true) {
          context.read<UserDataCubit>().clearUserData();
          context.read<AuthenticationCubit>().signOut();
          context.go(OnboardingScreen.path);
        }
      });
    }


    return Drawer(
        elevation: 2,
        child: Column(
          children: [
            DrawerHeader(
              child: Column(
                children: [
                  const CircleAvatar(
                    radius: 40,
                    child: Icon(
                      Icons.person_rounded,
                      size: 40,
                    ),
                  ),
                  const SizedBox(height: 8),
                  BlocBuilder<UserDataCubit, UserDataState>(
                    builder: (context, state) {
                      return Text(
                        state.member.fullName,
                        style: const TextStyle(
                          fontSize: theFontSize,
                        ),
                      );
                    },
                  ),
                ],
              ),
            ),
            Expanded(
              // Ensures that the rest of the list takes up available space
              child: ListView(
                children: [
                  ListTile(
                    title: const Text('Despre Aplicație'),
                    onTap: () {
                      context.push(AboutScreen.absolutePath);
                    },
                  ),
                ],
              ),
            ),
            // "Delogare" is placed here and will always be at the bottom
            ListTile(
              title: const Text('Delogare'),
              onTap: logout,
            ),
          ],
        ),
      );
  }
}
