import 'package:auto_asig/core/data/constants.dart';
import 'package:flutter/material.dart';

class AsigButton extends StatelessWidget {
  final bool isActive;
  final void Function()? onPressed;
  final String text;
  final Widget? preTextIcon;
  final TextStyle? textStyle;
  final EdgeInsetsGeometry? padding;
  final EdgeInsetsGeometry? margin;
  final Color? inactiveTextColor;
  final Color? inactiveBackgroundColor;
  final Color? activeTextColor; // Culoarea textului când butonul este activ
  final Color?
      activeBackgroundColor; // Culoarea fundalului când butonul este activ

  const AsigButton({
    required this.onPressed,
    required this.text,
    this.preTextIcon,
    this.isActive = true,
    this.textStyle,
    this.padding,
    this.margin,
    this.inactiveTextColor,
    this.inactiveBackgroundColor,
    this.activeTextColor,
    this.activeBackgroundColor,
    super.key,
  });

  @override
  Widget build(BuildContext context) {
    TextStyle finalTextStyle =
        textStyle ?? const TextStyle(fontSize: theFontSize);
    if (!isActive) {
      finalTextStyle = finalTextStyle.copyWith(
        color: inactiveTextColor ?? Colors.grey[500],
      );
    } else if (activeTextColor != null) {
      finalTextStyle = finalTextStyle.copyWith(color: activeTextColor);
    }
    return Container(
      padding: padding,
      margin: margin,
      width: double.infinity,
      child: ElevatedButton(
        onPressed: isActive ? onPressed : null,
        style: ElevatedButton.styleFrom(
          backgroundColor: isActive
              ? (activeBackgroundColor)
              : (inactiveBackgroundColor ?? Colors.grey),
          disabledForegroundColor:
              inactiveTextColor ?? Colors.grey.withOpacity(0.38),
          disabledBackgroundColor:
              inactiveBackgroundColor ?? Colors.grey.withOpacity(0.12),
        ),
        child: Row(
          mainAxisSize: MainAxisSize.min,
          mainAxisAlignment: MainAxisAlignment.center,
          children: [
            if (preTextIcon != null) preTextIcon!,
            if (preTextIcon != null) const SizedBox(width: 10),
            Flexible(
              child: Text(
                text,
                style: finalTextStyle,
                textAlign: TextAlign.center,
                softWrap: true,
                overflow: TextOverflow.visible,
              ),
            ),
          ],
        ),
      ),
    );
  }
}
