// reg_country_picker_cubit.dart

import 'package:auto_asig/feature/auth/cubit/reg_country_picker_state.dart';
import 'package:bloc/bloc.dart';
import 'package:fl_country_code_picker/fl_country_code_picker.dart';

class RegCountryPickerCubit extends Cubit<CountryPickerState> {
  RegCountryPickerCubit() : super(CountryPickerState.initial());

  void selectCountry(CountryCode country) {
    emit(state.copyWith(selectedCountry: country));
  }
}
