import 'package:auto_asig/core/app/app_data.dart';
import 'package:auto_asig/core/data/http_data.dart';
import 'package:auto_asig/feature/auth/cubit/registration_state.dart';
import 'package:firebase_auth/firebase_auth.dart';
import 'package:flutter_bloc/flutter_bloc.dart';

class RegistrationCubit extends Cubit<RegistrationState> {
  final FirebaseAuth _firebaseAuth;

  RegistrationCubit(this._firebaseAuth) : super(RegistrationInitial());

  Future<void> register(
      {required String email, required String password,
      required String firstName, required String lastName,
      required String phoneNumber,}) async {
    try {
      await _firebaseAuth
          .createUserWithEmailAndPassword(email: email, password: password)
          .then((user) {
        userCollection!.get().then((value) {
          if (value.docs.isNotEmpty) {
            // print('usersCount: ${value.docs.length}');
          }
          registerUser(
            user.user!.uid,
            email,
          );
        });
      });
      emit(RegistrationSuccess());
    } on FirebaseAuthException catch (e) {
      emit(RegistrationFailure(
          e.message ?? 'Eroare necunoscută la înregistrare'));
    }
  }
}
