import 'package:auto_asig/core/data/assistants.dart';
import 'package:auto_asig/core/data/constants.dart';
import 'package:auto_asig/core/widgets/custom_btn.dart';
import 'package:auto_asig/feature/auth/cubit/auth_cubit.dart';
import 'package:auto_asig/feature/auth/cubit/auth_state.dart';
import 'package:auto_asig/feature/auth/screens/forgot_password_screen.dart';
import 'package:auto_asig/feature/auth/screens/onboarding_screen.dart';
import 'package:auto_asig/feature/home_screen/screens/home_screen.dart';
import 'package:flutter/material.dart';
import 'package:flutter_bloc/flutter_bloc.dart';
import 'package:go_router/go_router.dart';

class LoginScreen extends StatelessWidget {
  LoginScreen({super.key});

  static const path = 'login_screen';
  static const absolutePath = '${OnboardingScreen.path}/$path';

  final TextEditingController emailController = TextEditingController();
  final TextEditingController passwordController = TextEditingController();

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: const Text('Autentificare'),
      ),
      body: BlocConsumer<AuthenticationCubit, AuthenticationState>(
        listener: (context, state) {
          if (state is AuthenticationSuccess) {
            context.go(HomeScreen.path);
          } else if (state is AuthenticationFailure) {
            showSnackbar(
              context,
              state.errorMessage,
            );
          }
        },
        builder: (context, state) {
          final isPasswordVisible = state is PasswordVisibilityState
              ? state.isPasswordVisible
              : false;

          return Padding(
            padding: const EdgeInsets.all(padding),
            child: Column(
              mainAxisAlignment: MainAxisAlignment.center,
              crossAxisAlignment: CrossAxisAlignment.stretch,
              children: <Widget>[
                TextField(
                  style: const TextStyle(fontSize: theFontSize),
                  controller: emailController,
                  decoration: const InputDecoration(
                    labelText: 'Email',
                    labelStyle: TextStyle(fontSize: theFontSize),
                  ),
                  keyboardType: TextInputType.emailAddress,
                ),
                const SizedBox(height: 20),
                TextField(
                  style: const TextStyle(fontSize: theFontSize),
                  controller: passwordController,
                  decoration: InputDecoration(
                    labelStyle: const TextStyle(fontSize: theFontSize),
                    labelText: 'Parola',
                    suffixIcon: IconButton(
                      icon: Icon(isPasswordVisible
                          ? Icons.visibility
                          : Icons.visibility_off),
                      onPressed: () {
                        BlocProvider.of<AuthenticationCubit>(context)
                            .togglePasswordVisibility();
                      },
                    ),
                  ),
                  obscureText: !isPasswordVisible,
                ),

                const SizedBox(height: 80),
                // Reset Button
                TextButton(
                  onPressed: () {
                    context.push(ForgotPasswordScreen.absolutePath);
                  },
                  child: Text(
                    'Ai uitat parola?',
                    style: TextStyle(
                      fontSize: theFontSize,
                      color: Theme.of(context).primaryColor,
                    ),
                  ),
                ),
              ],
            ),
          );
        },
      ),
      bottomNavigationBar: SafeArea(
        child: AsigButton(
          padding: const EdgeInsets.only(
            left: padding,
            right: padding,
            bottom: padding,
          ),
          onPressed: () {
            BlocProvider.of<AuthenticationCubit>(context).login(
              email: emailController.text,
              password: passwordController.text,
              context: context,
            );
          },
          text: 'Autentifică-te',
        ),
      ),
    );
  }
}
