import 'package:flutter/material.dart';
import 'package:intl/intl.dart';

class NotificationItem extends StatelessWidget {
  final int index;
  final DateTime selectedDate;
  final void Function(DateTime) onNotificationUpdate; // New parameter
  final VoidCallback onNotificationRemove;

  const NotificationItem({
    Key? key,
    required this.index,
    required this.selectedDate,
    required this.onNotificationUpdate, // Accepting the callback
    required this.onNotificationRemove,
  }) : super(key: key);

  @override
  Widget build(BuildContext context) {
    return Card(
      margin: const EdgeInsets.symmetric(vertical: 8),
      elevation: 3,
      shape: RoundedRectangleBorder(
        borderRadius: BorderRadius.circular(15),
      ),
      child: Padding(
        padding: const EdgeInsets.all(16),
        child: Column(
          crossAxisAlignment: CrossAxisAlignment.start,
          children: [
            // Date Picker for Notification (Styled as a Button)
            InkWell(
              onTap: () async {
                DateTime? newDate = await showDatePicker(
                  context: context,
                  initialDate: selectedDate,
                  firstDate: DateTime.now(),
                  lastDate: DateTime(2101),
                );
                if (newDate != null) {
                  onNotificationUpdate(newDate); // Using the callback
                }
              },
              borderRadius: BorderRadius.circular(8),
              splashColor: Colors.indigoAccent.withOpacity(0.1),
              child: Container(
                padding: const EdgeInsets.symmetric(horizontal: 12, vertical: 8),
                decoration: BoxDecoration(
                  color: Colors.indigoAccent.withOpacity(0.1), // Subtle background
                  borderRadius: BorderRadius.circular(8),
                ),
                child: Row(
                  children: [
                    const Icon(Icons.calendar_today_outlined, color: Colors.indigoAccent),
                    const SizedBox(width: 8),
                    Text(
                      DateFormat('dd.MM.yyyy').format(selectedDate), // Updated format to dd.MM.yyyy
                      style: const TextStyle(
                        fontSize: 16,
                        color: Colors.indigoAccent,
                        fontWeight: FontWeight.bold,
                        decoration: TextDecoration.underline, // Underline for interactivity
                      ),
                    ),
                  ],
                ),
              ),
            ),
            const SizedBox(height: 20),

            // Time Picker Button
            InkWell(
              onTap: () async {
                TimeOfDay? time = await showTimePicker(
                  context: context,
                  initialTime: TimeOfDay.fromDateTime(selectedDate),
                );
                if (time != null) {
                  DateTime updatedDateTime = DateTime(
                    selectedDate.year,
                    selectedDate.month,
                    selectedDate.day,
                    time.hour,
                    time.minute,
                  );
                  onNotificationUpdate(updatedDateTime); // Using the callback
                }
              },
              borderRadius: BorderRadius.circular(8),
              splashColor: Colors.indigoAccent.withOpacity(0.1),
              child: Container(
                padding: const EdgeInsets.symmetric(horizontal: 12, vertical: 8),
                decoration: BoxDecoration(
                  color: Colors.indigoAccent.withOpacity(0.1),
                  borderRadius: BorderRadius.circular(8),
                ),
                child: Row(
                  children: [
                    const Icon(Icons.access_time, color: Colors.indigoAccent),
                    const SizedBox(width: 8),
                    Text(
                      DateFormat('HH:mm').format(selectedDate),
                      style: const TextStyle(
                        fontSize: 16,
                        color: Colors.indigoAccent,
                        fontWeight: FontWeight.bold,
                      ),
                    ),
                  ],
                ),
              ),
            ),
            const Divider(height: 24),

            // Remove Notification Button
            Align(
              alignment: Alignment.centerRight,
              child: TextButton.icon(
                onPressed: onNotificationRemove, // Using the callback for removing
                icon: const Icon(Icons.delete_outline, color: Colors.redAccent),
                label: const Text(
                  'Remove',
                  style: TextStyle(
                    color: Colors.redAccent,
                    fontSize: 14,
                    fontWeight: FontWeight.bold,
                  ),
                ),
                style: TextButton.styleFrom(
                  padding: EdgeInsets.zero,
                  tapTargetSize: MaterialTapTargetSize.shrinkWrap,
                ),
              ),
            ),
          ],
        ),
      ),
    );
  }
}
