// Helper function to delete notification
  import 'package:flutter/material.dart';

void _deleteNotification(BuildContext context) {
    // context.read<BottomSheetDetailsCubit>().deleteNotification(index);
    showDialog(
      context: context,
      builder: (context) {
        return AlertDialog(
          title: const Text(
            'Ștergere Notificare',
            style: TextStyle(
              fontWeight: FontWeight.bold,
            ),
          ),
          icon: const Icon(
            Icons.warning,
            color: Colors.red,
            size: 40,
          ),
          content: const Text(
            'Ești sigur că dorești să ștergi această notificare? Această acțiune este ireversibilă.',
            style: TextStyle(
              color: Colors.red,
              fontWeight: FontWeight.bold,
            ),
          ),
          actions: [
            TextButton(
              onPressed: () {
                Navigator.of(context).pop();
              },
              child: const Text('Anulează'),
            ),
            TextButton(
              onPressed: () {
                // context.read<BottomSheetDetailsCubit>().deleteNotification(index);
                Navigator.of(context).pop();
              },
              child: const Text('Șterge'),
            ),
          ],
        );
      },
    );
  }
