import 'package:auto_asig/core/cubit/user_data_cubit.dart';
import 'package:auto_asig/core/data/constants.dart';
import 'package:auto_asig/core/widgets/custom_btn.dart';
import 'package:auto_asig/feature/auth/cubit/auth_cubit.dart';
import 'package:auto_asig/feature/auth/screens/onboarding_screen.dart';
import 'package:auto_asig/feature/cars_reg/screens/register_car_screen.dart';
import 'package:flutter/material.dart';
import 'package:flutter_bloc/flutter_bloc.dart';
import 'package:go_router/go_router.dart';

class HomeScreen extends StatelessWidget {
  const HomeScreen({super.key});

  static const path = '/home_screen';

  @override
  Widget build(BuildContext context) {
    final member = context.read<UserDataCubit>().state.member;

    if (screenHeight == null) {
      screenHeight = MediaQuery.of(context).size.height;
      screenWidth = MediaQuery.of(context).size.width;
    }

    void logout() {
      // ask for confirmation
      showDialog(
        context: context,
        builder: (context) => AlertDialog(
          title: const Text(
            'Delogare',
            style: TextStyle(fontSize: theFontSize),
          ),
          content: const Text(
            'Ești sigur că vrei să te deloghezi?',
            style: TextStyle(
              fontSize: theFontSize,
            ),
          ),
          actions: [
            TextButton(
              onPressed: () => Navigator.of(context).pop(false),
              child: const Text(
                'Nu',
                style: TextStyle(fontSize: theFontSize),
              ),
            ),
            TextButton(
              onPressed: () => Navigator.of(context).pop(true),
              child: const Text(
                'Da',
                style: TextStyle(
                  fontSize: theFontSize,
                ),
              ),
            ),
          ],
        ),
      ).then((value) {
        if (value == true) {
          context.read<UserDataCubit>().clearUserData();
          context.read<AuthenticationCubit>().signOut();
          context.go(OnboardingScreen.path);
        }
      });
    }

    return Scaffold(
      appBar: AppBar(
        // title: Text('Prezență $sportName - $date'),
        title: Text('Auto Asig - v0.0.1a'),
        // actions: [
        //   IconButton(
        //     icon: Icon(Icons.chat_rounded),
        //     color: !member.canUseChat ? Colors.grey : null,
        //     onPressed: () {
        //       if (member.canUseChat) {
        //         context.push(ChatScreen.absolutePath);
        //       } else {
        //         showSnackbar(
        //             context, 'Chat-ul este dezactivat pentru acest cont');
        //       }
        //     },
        //   ),
        // ],
      ),
      body: Container(
        padding: const EdgeInsets.only(
          left: padding,
          right: padding,
          top: 40,
        ),
        child: SingleChildScrollView(
          child: Column(
            children: [
              const SizedBox(height: 16),
              PrezButton(
                text: 'Înregistrare vehicul',
                onPressed: () {
                  // context.push(SelectDatesScreen.absolutePath);
                  context.push(RegisterCarScreen.absolutePath);
                },
                preTextIcon: const Icon(Icons.car_crash_rounded),
              ),
              const Padding(
                padding: EdgeInsets.symmetric(vertical: 8.0),
                child: Divider(),
              ),
            ],
          ),
        ),
      ),
      bottomNavigationBar: SafeArea(
        child: PrezButton(
          padding: const EdgeInsets.only(
            left: padding,
            right: padding,
            bottom: padding,
          ),
          onPressed: logout,
          text: 'Delogare',
        ),
      ),
    );
  }
}
