import 'package:auto_asig/core/data/constants.dart';
import 'package:flutter/material.dart';
import 'package:auto_asig/feature/home_screen/cubit/home_screen_cubit.dart';
import 'package:auto_asig/feature/home_screen/cubit/home_screen_state.dart';
import 'package:flutter_bloc/flutter_bloc.dart';

class HomeBottomNavigationBar extends StatelessWidget {
  final HomeScreenCubit homeCubit;

  const HomeBottomNavigationBar({super.key, required this.homeCubit});

  @override
  Widget build(BuildContext context) {
    return BlocBuilder<HomeScreenCubit, HomeScreenState>(
      builder: (context, state) {
        return BottomNavigationBar(
          currentIndex: state.activeTab.index,
          onTap: (index) {
            final tab = HomeScreenTabState.values[index];
            homeCubit.selectTab(tab);
          },
          selectedItemColor: Colors.blue, // Set selected tab color to blue
          unselectedItemColor: Colors.grey, // Optional: color for unselected tabs
          items: const [
            BottomNavigationBarItem(
              icon: Icon(Icons.person),
              label: 'Personal',
            ),
            BottomNavigationBarItem(
              icon: Icon(Icons.directions_car),
              label: 'Vehicule',
            ),
          ],
        );
      },
    );
  }
}
