import 'package:flutter/material.dart';

class NotifItem extends StatelessWidget {
  const NotifItem({super.key});

  @override
  Widget build(BuildContext context) {
    return Card(
                  // color: primaryBlue,
                  margin:
                      const EdgeInsets.symmetric(horizontal: 16, vertical: 8),
                  shape: RoundedRectangleBorder(
                    borderRadius: BorderRadius.circular(15.0),
                  ),
                  elevation: 4,
                  child: ListTile(
                    contentPadding: const EdgeInsets.all(16),
                    title: Text(
                      isExpired
                          ? '${reminder.title} - Expirat'
                          : reminder.title,
                      style: TextStyle(
                        fontSize: 18,
                        fontWeight: FontWeight.bold,
                        // color: isExpired ? Colors.red : Colors.white,
                        color: isExpired ? Colors.red : null,
                      ),
                    ),
                    subtitle: Column(
                      crossAxisAlignment: CrossAxisAlignment.start,
                      children: [
                        Text(
                          '(${convertReminderTypeToString(reminder.type)})',
                          style: const TextStyle(
                            fontStyle: FontStyle.italic,
                            // color: Colors.white,
                            // color: primaryYellow,
                            fontWeight: FontWeight.bold,
                          ),
                        ),
                        const SizedBox(height: 8),
                        Text(
                          isExpired
                              ? 'Expirat de $days zile.'
                              : 'Timp Rămas: $days zile.',
                          style: TextStyle(
                            color: remainingTime < 7 ? Colors.red : primaryBlue,
                            fontWeight: remainingTime < 7
                                ? FontWeight.bold
                                : FontWeight.normal,
                          ),
                        ),
                        const SizedBox(height: 8),
                        Text(
                          'Data Expirarii: ${dateFormatter.format(reminder.expirationDate)}',
                          style: const TextStyle(
                            // color: Colors.white,
                            fontWeight: FontWeight.bold,
                          ),
                        ),
                        const SizedBox(height: 8),
                        if (reminder.notificationDates.isNotEmpty)
                          const Text(
                            'Notificari:',
                            style: TextStyle(
                              // color: Colors.white,
                              fontWeight: FontWeight.bold,
                            ),
                          ),
                        for (var date in reminder.notificationDates)
                          Text(
                            '• ${dateFormatter.format(date)} at ${timeFormatter.format(date)}',
                            style: const TextStyle(
                              // color: Colors.white,
                              fontWeight: FontWeight.bold,
                            ),
                          ),
                      ],
                    ),
                    trailing: const Icon(
                      Icons.arrow_forward_ios,
                      // color: primaryYellow,
                    ),
                    onTap: () {
                      // Handle tapping on the card, e.g., open a detailed view
                    },
                  ),
                );
  }
}
