import 'package:flutter/material.dart';

class ProgressListTile extends StatelessWidget {
  final String title;
  final String progressTitle;
  final double progressValue; // Value between 0 and 100

  const ProgressListTile({
    Key? key,
    required this.title,
    required this.progressTitle,
    required this.progressValue,
  }) : super(key: key);

  @override
  Widget build(BuildContext context) {
    return Card(
      shape: RoundedRectangleBorder(
        borderRadius: BorderRadius.circular(15.0),
      ),
      elevation: 3, // Optional: adds a subtle shadow for the card
      child: ListTile(
        contentPadding: const EdgeInsets.all(16),
        title: Text(
          title,
          style: const TextStyle(fontWeight: FontWeight.bold),
        ),
        subtitle: Column(
          crossAxisAlignment: CrossAxisAlignment.start,
          children: [
            const SizedBox(height: 8), // Space between title and progress bar
            Text(
              progressTitle,
              style: const TextStyle(fontSize: 12, color: Colors.black54),
            ),
            const SizedBox(height: 4),
            ClipRRect(
              borderRadius: BorderRadius.circular(8),
              child: LinearProgressIndicator(
                value: (progressValue > 60 ? 1.0 : progressValue / 60),
                minHeight: 8,
                backgroundColor: Colors.grey[300],
                valueColor: AlwaysStoppedAnimation<Color>(_getProgressColor()),
              ),
            ),
          ],
        ),
      ),
    );
  }

  Color _getProgressColor() {
    if (progressValue > 60) {
      return Colors.green;
    } else if (progressValue > 20) {
      return Colors.yellow;
    } else {
      return Colors.red;
    }
  }
}
