import 'package:flutter/material.dart';

class VehicleReminderItem extends StatelessWidget {
  final String reminderTitle;
  final String expirationDate;
  final int daysLeft;
  final String time;

  const VehicleReminderItem({
    required this.reminderTitle,
    required this.expirationDate,
    required this.daysLeft,
    required this.time,
    super.key,
  });

  @override
  Widget build(BuildContext context) {
    return ListTile(
      leading: Icon(Icons.car_rental,
          color: daysLeft <= 0 ? Colors.red : Colors.green),
      title: Text(reminderTitle),
      subtitle: Text('Expira in: $expirationDate'),
      trailing: Text(daysLeft > 0 ? '$daysLeft zile ramase' : 'Expirat'),
    );
  }
}
