import 'package:bloc/bloc.dart';
import 'package:equatable/equatable.dart';

part 'id_cards_state.dart';

class IdCardsCubit extends Cubit<IdCardsState> {
  IdCardsCubit() : super(IdCardsInitial());

  void updateName(String name) {
    emit(state.copyWith(name: name));
  }

  void updateExpirationDate(DateTime expirationDate) {
    emit(state.copyWith(expirationDate: expirationDate));
  }
}
